tinymce.PluginManager.add('ex_loadfile', function (editor, url) {
    editor.addButton('ex_loadfile', {
        text: 'Load files',
        icon: false,
        onclick: function () {
            // Open window
            editor.windowManager.open({
                title: 'File manager',
                file: url + '/dialog.html',
                width: 400,
                height: 300,
                inline: 1,
//                onsubmit: function (e) {
//                    // Insert content when the window form is submitted
//                    editor.insertContent('Title: ' + e.data.title);
//                }
            });
        }

    });
});